import React, { useState, useEffect } from 'react';
import { useGlobalState } from './global-state-provider';
import LoadingPage from './components/loading-indicators/loading-page';
import MainPage from './components/main-page/main-page';

const isLoadingClassName = 'is-loading-page';
const cvContentWrapperClassName = 'cv-content-wrapper';

const PageContentWrapper = () => {
    const { isPageLoading, shouldDisplayLoadingPage } = useGlobalState();
    const [ pageContentWrapperClassName, setPageContentWrapperClassName ] = useState(isLoadingClassName);
  
    useEffect(() => {
      if (!isPageLoading) {
        setPageContentWrapperClassName(cvContentWrapperClassName);
      }
    }, [ isPageLoading ]);

    const content = shouldDisplayLoadingPage
      ? <LoadingPage />
      : <MainPage />
    
    return (
        <body className={pageContentWrapperClassName}>
            {content}
        </body>
    )
};

export default PageContentWrapper;
