import React, { useState, useEffect } from 'react';

const LoadingIndicator = () => {
    const [ isLoading, setIsLoading ] = useState(true);

    useEffect(() => {
        setTimeout(() => {
            setIsLoading(false);
        }, 3000);
    }, []);

    return (
        <section>
            {
                isLoading
                    ? <h1>sdasdsa</h1>
                    : <h2>nananna</h2>
            }
        </section>
    )
};

export default LoadingIndicator;
