import './portfolio-item.scss';

import React, { useState } from 'react';

const portfolioItemDescwriptionWrapperClassName = 'portfolio-item-description-wrapper';

const PortfolioItem = ({ modalNumber, image, alt }) => {
    const [ hover, setHover ] = useState(false);

    const onMouseOver = () => setHover(true);
    const onMouseOut = () => setHover(false);

    const customClassName = hover ?
        `${portfolioItemDescwriptionWrapperClassName} hovered`
        : portfolioItemDescwriptionWrapperClassName;
    
    return (
        <div
          className="portfolio-item-wrapper"
          onMouseOver={onMouseOver}
          onMouseOut={onMouseOut}
        >
            <div className="portfolio-item">
                <a href={modalNumber} title>
                    <img alt={alt} src={image} />
                    <div className={customClassName}>
                        <div className="portfolio-item-description">
                            <h5>Projects SoftUni</h5>
                            <p>Web Development</p>
                        </div>
                        <div className="link-icon">
                            <i className="icon-plus" />
                        </div>
                    </div>
                </a>
            </div>
        </div>
    );
};

export default PortfolioItem;
