const increaseNumber = (startNumber, numberToIncrementWith, setStartNumber, timeoutInMs, maxNumber) => {
    const updatedNumber = startNumber + numberToIncrementWith;

    const timeout = setTimeout(
        () => setStartNumber(updatedNumber),
        timeoutInMs,
    );

    if (updatedNumber > maxNumber) {
        return clearTimeout(timeout);
    }
};

export {
    increaseNumber,
}
