import './project-tab-content.scss';

import React from 'react';
import Modal from 'react-modal';
import ProjectTabContent from './project-tab-content';
import OutsideClickHandler from 'react-outside-click-handler';

const ProjectTabContentModal = ({ isOpen, name, src, description, onClickHandler }) => (
    <OutsideClickHandler onOutsideClick={onClickHandler}>
        <Modal
          className="project-tab-content-modal"
          isOpen={isOpen}>
            <ProjectTabContent
              name={name}
              src={src}
              description={description}
            />
        </Modal>
    </OutsideClickHandler>
);

export default ProjectTabContentModal;
